import React, { useState } from "react";
import Board from "./Board";
import { calculateWinner } from "../helpers";
// import Toast from "react-bootstrap/Toast";

const style = {
  width: "200px",
  margin: "20px auto"
};

function Game() {
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [stepNumber, setStepNumber] = useState(0);
  const [xIsNext, setXIsNext] = useState(true);
  const winner = calculateWinner(history[stepNumber]);

  const handleClick = i => {
    //slice out history we dont need, like state aftward, because the state will be changing
    const timeInHistory = history.slice(0, stepNumber + 1);
    const current = timeInHistory[stepNumber];
    //clone of the state we will be changing
    const squares = [...current];
    //If user clicks on an occupied square or if there is a winner, return
    //else if xIsNext is true then "X" otherwise "O"
    //setHistory state values
    if (winner || squares[i]) return;
    squares[i] = xIsNext ? "X" : "O";
    setHistory([...timeInHistory, squares]);
    setStepNumber(timeInHistory.length);
    setXIsNext(!xIsNext);
  };

  const jumpTo = step => {
    setStepNumber(step);
    setXIsNext(step % 2 === 0);
  };

  const renderMoves = () =>
    history.map((_step, move) => {
      const destination = move ? `Go to move#${move}` : "Go to Start";
      return (
        <li key={move}>
          <button onClick={() => jumpTo(move)}>{destination}</button>
        </li>
      );
    });

  return (
    <div>
      <Board squares={history[stepNumber]} onClick={handleClick} />
      <div style={style}>
        <p>
          {winner ? "Winner " + winner : "Next Player " + (xIsNext ? "X" : "O")}
          {renderMoves()}
        </p>
      </div>
      <Toast>
        <Toast.Header>
          <img src="holder.js/20x20?text=%20" className="rounded mr-2" alt="" />
          <strong className="mr-auto">Winner!{winner}</strong>
        </Toast.Header>
        <Toast.Body>Game Over, {winner} has won the game!</Toast.Body>
      </Toast>
    </div>
  );
}
export default Game;
